      breaks <- scale$get_transformation()$breaks
      n_breaks <- scale$n.breaks
      if (!is.null(n_breaks) && "n" %in% fn_fmls_names(breaks)) {
        self$breaks <- function(x) breaks(x, n = n_breaks)
      } else {
        self$breaks <- breaks
      }
    }
  }
  if (is.derived(self$labels)) self$labels <- scale$labels
  if (is.derived(self$guide)) self$guide <- scale$guide

  # <<< start addition
  # If breaks are a named vector and labels are defaulted/derived, use names as labels
  if (scale$is_discrete() && (is.derived(self$labels) || is.waiver(self$labels))) {
    if (is.atomic(self$breaks) && !is.null(names(self$breaks))) {
      names_vec <- names(self$breaks)
      self$labels <- function(x) {
        ix <- match(x, self$breaks)
        out <- names_vec[ix]
        out[is.na(out)] <- as.character(x[is.na(out)])
        out
      }
    }
  }
  # <<< end addition
},
```

